let TracesPlusPageviews = 
    traces 
    | union pageViews 
    | where timestamp between (_startTime .. _endTime)
    | count 
    | extend Desc = 'traces + pageViews'; 
let BaseCustomers = 
    Base_Customers 
    | count 
    | extend Desc = 'Base_Customers';
TracesPlusPageviews
| union BaseCustomers

